import React from "react";
import { IconButton, Menu, MenuButton, MenuItem, MenuList } from "@chakra-ui/react";
import { TimeIcon } from "@chakra-ui/icons";

type Props = {
  settingTime: number;
  setSettingTime: React.Dispatch<React.SetStateAction<number>>;
};

const SettingTime = ({ settingTime, setSettingTime }: Props) => {
  return (
    <Menu>
      <MenuButton
        as={IconButton}
        icon={<TimeIcon />}
        variant="ghost"
        colorScheme="ghost"
        _focus={{ boxShadow: "none" }}
      />
      <MenuList>
        <MenuItem
          onClick={() => {
            console.log("1:00 が押されたよ");
            setSettingTime(60);
          }}
        >
          1:00
        </MenuItem>
        <MenuItem>3:00</MenuItem>
        <MenuItem>5:00</MenuItem>
        <MenuItem>10:00</MenuItem>
      </MenuList>
    </Menu>
  );
};

export default SettingTime;
